import React, { useEffect, useState } from "react";
import Logo from "./assets/logo.png";
import control from "./assets/control.png";

import { Calendar, Chart } from "./assets";

function App() {
  const [open, setOpen] = useState(true);
  const [images, setImages] = useState([]);
  const Menus = [
    { title: "Dashboard", src: Calendar },
    { title: "Inbox", src: "Chat" },
    { title: "Accounts", src: "User" },
    { title: "Schedule", src: "Calendar" },
    { title: "Search", src: "Search" },
    { title: "Analytics", src: "Chart" },
    { title: "Files", src: "Folder" },
    { title: "Setting", src: "Setting" },
  ];

  useEffect(() => {
    setImages([Calendar, Chart]);
  }, []);

  return (
    <div className="flex">
      <div
        className={`${
          open ? "w-72" : "w-20"
        } duration-300 p-5 pt-8 h-screen bg-dark-purple relative`}
      >
        <img
          src={control}
          className={`absolute cursor-pointer rounded-full -right-3 top-9 w-7 border-2 border-dark-purple ${
            !open ? "rotate-180" : ""
          }`}
          onClick={() => setOpen(!open)}
          alt="Control"
        />
        <div className="flex gap-x-4 item-center">
          <img
            src={Logo}
            alt="logo"
            className={`cursor-pointer duration-500`}
          />
          <h1
            className={`text-white orign-left font-medium text-xl mt-1 duration-300 ${
              !open && "scale-0"
            }`}
          >
            Designer
          </h1>
        </div>
        <ul className="pt-6">
          {Menus.map((menu, index) => (
            <li
              key={index}
              className="text-gray-300 text-sm flex item-center gap-x-4 cursor-pointer"
            >
              {images.map((i) => (
                <img src={i} alt="" />
              ))}
              <span>{menu.title}</span>
            </li>
          ))}
        </ul>
      </div>
      <div className="p-7 text-2xl font-semibold flex-1 h-screen">
        Home Page
      </div>
    </div>
  );
}

export default App;
