import React, { useState } from "react";
import {
  Calendar,
  Chart,
  Chat,
  Chart_fill,
  control,
  Folder,
  logo,
  Setting,
  Search,
  User,
} from "./assets/index.js";
import { Link } from "react-router-dom";
import Pagesrouter from "./components/routes/pagesrouter.js";

function App() {
  const [open, setOpen] = useState(true);
  const Menus = [
    { title: "Dashboard", to: "/dashboard", src: Chart_fill },
    { title: "Inbox", to: "/inbox", src: Chat },
    { title: "Accounts", to: "/accounts", src: User, gap: true },
    { title: "Schedule", to: "/schedule", src: Calendar },
    { title: "Search", to: "/search", src: Search },
    { title: "Analytics", to: "/analytics", src: Chart },
    { title: "Files", to: "/files", src: Folder, gap: true },
    { title: "Setting", to: "/setting", src: Setting },
  ];

  return (
    <div className="flex">
      <div
        className={`${
          open ? "w-72" : "w-20"
        } duration-300 p-5 pt-8 h-screen bg-dark-purple relative`}
      >
        <img
          src={control}
          className={`absolute cursor-pointer rounded-full -right-3 top-9 w-7 border-2 border-dark-purple ${
            !open ? "rotate-180" : ""
          }`}
          onClick={() => setOpen(!open)}
          alt="Control"
        />
        <div className="flex gap-x-4 item-center">
          <img
            src={logo}
            alt="logo"
            className={`cursor-pointer duration-500 ${
              open && "rotate-[360deg]"
            }`}
          />
          <h1
            className={`text-white origin-left font-medium text-xl mt-2 duration-300 ${
              !open && "scale-0"
            }`}
          >
            Designer
          </h1>
        </div>
        <ul className="pt-6">
          {Menus.map((menu, index) => (
            <Link to={menu.to}
            className={`${
              !open ? "hidden" : "duration-200"
            } text-gray-300 text-sm flex item-center gap-x-4 cursor-pointer p-2  rounded-md 
          ${menu.gap ? "mt-9" : "mt-2"} ${index === 0 && "bg-light-white"}`}
            className=" hover:bg-light-white flex gap-x-2 items-center">
              <li
                key={index}
                className={`${
                  !open ? "hidden" : "duration-200"
                } text-gray-300 text-sm flex item-center gap-x-4 cursor-pointer p-2  rounded-md 
              ${menu.gap ? "mt-9" : "mt-2"} ${index === 0 && "bg-light-white"}`}
              >
                <img src={menu.src} alt="icon" />
                {menu.title}
              </li>
            </Link>
          ))}
        </ul>
      </div>

      <div className="p-7 text-2xl font-semibold flex-1 h-screen">
        <div>
          {" "}
          <h1>Navbar</h1>{" "}
        </div>
        <Pagesrouter />
      </div>
    </div>
  );
}

export default App;
